const fetchContract = async (signerOrProvider) => {

    const web3Modal = new Web3Modal();
    const connection = await web3Modal.connect();
    const provider = new ethers.providers.Web3Provider(connection);
    const { chainId } = await provider.getNetwork();
    const ftContract = new ethers.Contract(
        "0x5404DB87A5328015617594efDEbD252e45015581",
        abi.abi,
        signerOrProvider
    );
    return ftContract;
};



const connectingWithSmartContract = async () => {
    try {
        const web3Modal = new Web3Modal();
        const connection = await web3Modal.connect();
        const provider = new ethers.providers.Web3Provider(connection);
        const signer = provider.getSigner();

        const contract = fetchContract(signer);
        return contract;
    } catch (error) {
        console.log(
            `Ran into an error while connecting to Smart Contract ${error}`
        );
    }
};




export function AppContext({ children }) {

    const [test,setTest]=useState('shobhit')
    const [success, setSuccess] = useState("");
    const [openSuccess, setOpenSuccess] = useState(false);
    const [error, setError] = useState("");
    const [openError, setOpenError] = useState(false);
    const [currentAccount, setCurrentAccount] = useState("");
    const router = useRouter();
    const [name, setName] = useState("hiii");
    const ConnectToWallet = async () => {
        try {
            if (!window.ethereum)
                return setOpenError(true), setError("Please, Install a Wallet");

            const accounts = await window.ethereum.request({
                method: "eth_accounts",
            });

            if (accounts.length) {
                setCurrentAccount(accounts[0]);
            } else {
                setError("Please connect to your account");
                setOpenError(true);
            }
        } catch (error) {
            setError("Wallet is not connected");
            setOpenError(true);
        }
    };

    const checkWalletNetwork = async () => {
        const web3Modal = new Web3Modal();
        const connection = await web3Modal.connect();
        const provider = new ethers.providers.Web3Provider(connection);

        const { chainId } = await provider.getNetwork();

        if (chainId === 31337 || chainId === 80001 || chainId === 5 || chainId === 1337) {
            return true;
        } else {
            return false;
        }
    };

    const connectWallet = async () => {
        try {
            if (!window.ethereum)
                return setOpenError(true), setError("Install MetaMask");

            const accounts = await window.ethereum.request({
                method: "eth_requestAccounts",
            });

            setCurrentAccount(accounts[0]);
            connectingWithSmartContract();

            setSuccess("Successfully Connected to Wallet");
            setOpenSuccess(true);
        } catch (error) {
            setError("Error while connecting to Wallet");
            setOpenError(true);
        }
    };

    const mintCertificate = async (recipient, name, description, imageURL) => {
      try {

        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contractAddress= "0x5404DB87A5328015617594efDEbD252e45015581";
        const contract = new ethers.Contract(contractAddress, abi.abi, signer);
        const tx = await contract.mintCertificate(recipient, name, description, imageURL);
        await tx.wait();
        console.log(tx)
        alert('Certificate minted successfully!');
      } catch (error) {
        console.error(error);
        alert('Error minting certificate');
      }
    };


    const [certificates, setCertificates] = useState([]);
    const fetchCertificates = async (address) => {
      try {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contractAddress= "0x5404DB87A5328015617594efDEbD252e45015581";
        const contract = new ethers.Contract(contractAddress, abi.abi, signer);
        const certificates = await contract.fetchCertificateOf(address);
        setCertificates(certificates);
        console.log(certificates)
      } catch (error) {
        console.error(error);
        alert('Error fetching certificates');
      }
    };

    return (
        <GovContext.Provider value={{ ConnectToWallet, connectWallet, currentAccount,  mintCertificate,fetchCertificates ,certificates,}}>
            {children}
        </GovContext.Provider>
    );
}